import React, { useState } from 'react';
import axios from 'axios';

function KYCProcess() {
    const [step, setStep] = useState(1);
    const [driverInfo, setDriverInfo] = useState({ name: '', phone: '+91', email: 'hello@mail.com' });
    const [driverLicense, setDriverLicense] = useState(null);
    const [idCard, setIdCard] = useState(null);
    const [selfie, setSelfie] = useState(null);
    const [statusMessage, setStatusMessage] = useState('');
    const [dateOfExpiry, setDateOfExpiry] = useState('');
    const [idType, setIdType] = useState('');
    const [status, setStatus] = useState('');

    const headers = {
        appId: 'yvdxyy',
        appKey: '9epukpvlpl1asl3bym4j',
        transactionId: 'driverkyc'
    };

    const handleFileUpload = (e, setFile) => {
        const file = e.target.files[0];
        if (file && ['image/jpeg', 'image/png', 'application/pdf'].includes(file.type)) {
            setFile(file);
        } else {
            alert('Invalid file type. Please upload JPG, PNG, or PDF.');
        }
    };

    const validateDL = async () => {
        setStatus('Validating Driver License...');
        const formData = new FormData();
        formData.append('image', driverLicense);
        formData.append('countryId', 'ind');
        formData.append('documentId', 'dl');
        formData.append('expectedDocumentSide', 'front');

        try {
            const response = await axios.post('https://ind.idv.hyperverge.co/v1/readId', formData, { headers });
            const details = response.data?.result?.details?.[0]?.fieldsExtracted;

            if (details?.dateOfExpiry?.value) {
                const expiryDate = details.dateOfExpiry.value;
                setDateOfExpiry(expiryDate);
                const expiryDateObj = new Date(expiryDate.split('-').reverse().join('-'));
                if (expiryDateObj > new Date()) {
                    setStatus('Driver License validated successfully!');
                    return true;
                } else {
                    setStatus('Driver License is expired.');
                    return false;
                }
            } else {
                setStatus('Unable to fetch Driver License details.');
                return false;
            }
        } catch (error) {
            console.error('Error validating Driver License:', error);
            setStatus('Validation failed.');
            return false;
        }
    };

    const validateIDCard = async () => {
        // Ensure both the ID card and ID type are provided
        if (!idCard || !idType) {
            setStatus('Please provide both ID card and select a valid ID type.');
            return false;
        }

        setStatus('Validating ID Card...');
        const formData = new FormData();
        formData.append('file', idCard); // Append the ID card file
        formData.append('countryId', 'ind'); // Ensure the countryId is correct

        // Map selected ID type to the corresponding document ID
        const documentIdMap = {
            aadhaar: 'id',        // Aadhaar is 'id' in the document API
            pan: 'pan',           // PAN is 'pan'
            passport: 'passport', // Passport is 'passport'
        };

        // Get document ID based on the selected ID type (convert to lowercase)
        const documentId = documentIdMap[idType.toLowerCase()];

        // Check if the document ID is valid
        if (!documentId) {
            setStatus('Invalid document type selected.');
            return false;
        }

        // Append document ID and expected document side to form data
        formData.append('documentId', documentId); // Use mapped document ID
        formData.append('expectedDocumentSide', 'front'); // Assuming we are checking the front side

        try {
            // Send request to validate the ID card using the HyperVerge API
            const response = await axios.post('https://ind.idv.hyperverge.co/v1/readId', formData, { headers });
            console.log('API Response:', response.data);

            // Extract details from the API response
            const details = response.data?.result?.details?.[0]?.fieldsExtracted;

            // Check if the necessary fields (fullName, dateOfBirth, idNumber) are extracted
            if (details) {
                const { fullName, dateOfBirth, idNumber } = details;

                // Validate if all the required details are present
                if (fullName && dateOfBirth && idNumber) {
                    setStatus(`${idType.charAt(0).toUpperCase() + idType.slice(1)} validated successfully!`);
                    setStep(4); // Proceed to the next step after successful ID validation
                    return true;
                } else {
                    setStatus('Missing important details in the ID card.');
                    return false;
                }
            } else {
                setStatus('Unable to fetch ID card details.');
                return false;
            }
        } catch (error) {
            console.error('Error validating ID Card:', error);
            setStatus('Validation failed.');
            return false;
        }
    };


        const validateSelfie = async () => {
        if (!selfie) return false;
        setStatus('Validating selfie...');
        const formData = new FormData();
        formData.append('file', selfie);

        try {
            const response = await axios.post('https://ind.idv.hyperverge.co/v1/checkLiveness', formData, { headers });
            return response.data.isLive;
        } catch (error) {
            console.error('Error validating selfie:', error);
            setStatus('Selfie validation failed.');
            return false;
        }
    };

    const matchFace = async () => {
        const formData = new FormData();
        formData.append('selfie', selfie);
        formData.append('idCard', idCard);

        try {
            const response = await axios.post('https://ind.idv.hyperverge.co/v1/matchFace', formData, { headers });
            return response.data.isMatch;
        } catch (error) {
            console.error('Error matching face:', error);
            setStatus('Face match failed.');
            return false;
        }
    };

    const handleSubmitDL = async () => {
        setStatusMessage('Validating Driver License...');
        const isDLValid = await validateDL();

        if (isDLValid) {
            setStep(3);
            setStatusMessage(`Driver's License is valid.`);
        } else {
            setStatusMessage(`Validation failed`);
        }
    };

    const handleSubmitID = async () => {
        setStatusMessage('Validating ID Card...');
        const isIDValid = await validateIDCard();

        if (isIDValid) {
            setStep(4); 
        } else {
            setStatusMessage('ID Card validation failed.');
        }
    };

    const handleSubmitSelfie = async () => {
        setStatusMessage('Validating selfie...');
        const isSelfieValid = await validateSelfie();
        const isFaceMatch = await matchFace();
        if (isSelfieValid && isFaceMatch) {
            setStatusMessage('KYC process completed successfully.');
            setStep(5);
        } else {
            setStatusMessage('Selfie verification or face match failed.');
        }
    };

    return (
        <div>
            <h2>Driver KYC Process</h2>

            {step === 1 && (
                <div>
                    <h3>Step 1: Basic Information</h3>
                    <input 
                        type="text" 
                        placeholder="Name" 
                        onChange={(e) => setDriverInfo({ ...driverInfo, name: e.target.value })} 
                    />
                    <input 
                        type="text" 
                        placeholder="Phone" 
                        value={driverInfo.phone} 
                        onChange={(e) => e.target.value.length <= 13 && setDriverInfo({ ...driverInfo, phone: e.target.value })} 
                    />
                    <input 
                        type="email" 
                        placeholder="Email" 
                        value={driverInfo.email} 
                        onChange={(e) => setDriverInfo({ ...driverInfo, email: e.target.value })} 
                    />
                    <button onClick={() => setStep(2)}>Next</button>
                </div>
            )}

            {step === 2 && (
                <div>
                    <h3>Step 2: Upload Driver's License</h3>
                    <input 
                        type="file" 
                        accept=".jpg,.png,.pdf" 
                        onChange={(e) => handleFileUpload(e, setDriverLicense)} 
                    />
                    <button onClick={handleSubmitDL}>Submit</button>
                </div>
            )}

            {step === 3 && (
                <div>
                    <h3>Step 3: Upload ID Card</h3>
                    <label>Select ID Type:</label>
                    <select value={idType} onChange={(e) => setIdType(e.target.value)}>
                        <option value="">--Select--</option>
                        <option value="aadhaar">Aadhaar</option>
                        <option value="passport">Passport</option>
                        <option value="pan">PAN</option>
                    </select>
                    <input 
                        type="file" 
                        accept=".jpg,.png,.pdf" 
                        onChange={(e) => handleFileUpload(e, setIdCard)} 
                    />
                    <button onClick={handleSubmitID}>Submit</button>
                </div>
            )}

            {step === 4 && (
                <div>
                    <h3>Step 4: Upload Selfie</h3>
                    <input 
                        type="file" 
                        accept=".jpg,.png" 
                        onChange={(e) => handleFileUpload(e, setSelfie)} 
                    />
                    <button onClick={handleSubmitSelfie}>Submit</button>
                </div>
            )}

            <p>{statusMessage}</p>
        </div>
    );
}

export default KYCProcess;
