import React, { useState } from 'react';
import axios from 'axios';

const DriverKYC = () => {
  const [driverLicense, setDriverLicense] = useState(null);
  const [idCard, setIdCard] = useState(null);
  const [selfie, setSelfie] = useState(null);
  const [status, setStatus] = useState('');
  const [step, setStep] = useState(1);
  const [selectedDocument, setSelectedDocument] = useState('');
  const [driverName, setDriverName] = useState('');
  const [phoneNo, setPhoneNo] = useState('+91');
  const [age, setAge] = useState('');
  const [responseSummary, setResponseSummary] = useState('');

  const headers = {
    appId: 'yvdxyy',
    appKey: '9epukpvlpl1asl3bym4j',
    transactionId: 'driverkyc'
  };

  const handleFileUpload = (e, setFile) => {
    const file = e.target.files[0];
    if (file && ['image/jpeg', 'image/png', 'application/pdf'].includes(file.type)) {
      setFile(file);
    } else {
      alert('Invalid file type. Please upload jpg, png, or pdf.');
    }
  };

  const validateDL = async () => {
    setStatus('Validating Driver\'s License...');
    const formData = new FormData();
    formData.append('image', driverLicense);
    formData.append('countryId', 'ind');
    formData.append('documentId', 'dl');
    formData.append('expectedDocumentSide', 'front');

    try {
      const response = await axios.post('https://ind.idv.hyperverge.co/v1/readId', formData, { headers });
      if (response.data.result.action === 'pass' && response.data.result.status === 'success') {
        setResponseSummary(`License No: ${response.data.result.details[0].fieldsExtracted.idNumber.value}`);
        setStep(2);
      } else {
        setStatus('Validation failed.');
      }
    } catch (error) {
      setStatus('Error validating Driver\'s License.');
    }
  };

  const validateID = async () => {
    setStatus('Validating ID Card...');
    const formData = new FormData();
    formData.append('image', idCard);
    formData.append('countryId', 'ind');

    const documentIdMap = {
      Aadhaar: 'id',
      Passport: 'passport',
      'PAN Card': 'pan',
    };
    formData.append('documentId', documentIdMap[selectedDocument]);
    formData.append('expectedDocumentSide', 'front');

    try {
      const response = await axios.post('https://ind.idv.hyperverge.co/v1/readId', formData, { headers });
      if (response.data.result.action === 'pass' && response.data.result.status === 'success') {
        setResponseSummary(`Full Name: ${response.data.result.details[0].fieldsExtracted.fullName.value}`);
        setStep(3);
      } else {
        setStatus('Validation failed.');
      }
    } catch (error) {
      setStatus('Error validating ID Card.');
    }
  };

  const validateSelfie = async () => {
    setStatus('Validating Selfie...');
    const formData = new FormData();
    formData.append('image', selfie);

    try {
      const response = await axios.post('https://ind.idv.hyperverge.co/v1/checkLiveness', formData, { headers });
      if (response.data.result.action === 'pass' && response.data.result.status === 'success') {
        setResponseSummary('Selfie is live and valid.');
        setStep(4);
      } else {
        setStatus('Validation failed.');
      }
    } catch (error) {
      setStatus('Error validating selfie.');
    }
  };

  const matchFace = async () => {
    setStatus('Matching Face...');
    const formData = new FormData();
    formData.append('selfie', selfie);
    formData.append('id', driverLicense);

    try {
      const response = await axios.post('https://ind.idv.hyperverge.co/v1/matchFace', formData, { headers });
      if (response.data.result.action === 'pass' && response.data.result.status === 'success') {
        setResponseSummary('Face match successful.');
        setStatus('KYC Process Successful!');
      } else {
        setStatus('Face match failed.');
      }
    } catch (error) {
      setStatus('Error in face match process.');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (step === 1) await validateDL();
    else if (step === 2) await validateID();
    else if (step === 3) await validateSelfie();
    else if (step === 4) await matchFace();
  };

  return (
    <div className="kyc-container">
      <h1>Driver KYC Process</h1>
      <form onSubmit={handleSubmit} className="kyc-form">
        {responseSummary && <p className="response-summary">Summary: {responseSummary}</p>}
        <button type="submit">Continue</button>
      </form>
      <p className="status-message">Status: {status}</p>
    </div>
  );
};

export default DriverKYC;
