import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

axios.defaults.baseURL = 'http://localhost:5000'; // Adjust to your backend URL

const Bookticket = () => {
  const [userId, setUserId] = useState('');
  const [busId, setBusId] = useState(''); // Assuming you will set this from props or context
  const [seatNumber, setSeatNumber] = useState(1); // Default seat number
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const seatPrice = 10; // Price for one seat
  const navigate = useNavigate();

  // Fetch user data (assuming you have a way to get userId)
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/users'); // Adjust the endpoint as needed
        setUserId(response.data.userId); // Assuming the response contains userId
      } catch (error) {
        setMessage(getErrorMessage(error));
      }
    };

    fetchUserData();
  }, []);

  // Helper function to format error messages
  const getErrorMessage = (error) => {
    if (error.response) {
      return error.response.data.message || 'An error occurred on the server.';
    } else if (error.request) {
      return 'No response from the server. Please try again later.';
    }
    return `Unexpected error: ${error.message}`;
  };

  // Calculate total price
  const totalPrice = seatNumber * seatPrice;

  // Handle ticket booking
  const handleBooking = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('Booking ticket...');

    try {
      const response = await axios.post(`/api/tickets/book/${busId}`, {
        userId,
        seatNumber,
      });

      if (response.status >= 200 && response.status < 300) {
        setMessage(response.data.message || 'Ticket booked successfully!');
        navigate('/ticketlist'); // Redirect to ticket list after booking
      } else {
        setMessage(`Unexpected response status: ${response.status}`);
      }
    } catch (error) {
      console.error('Booking Error:', error);
      setMessage(getErrorMessage(error));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>Book a Ticket</h1>
      <form onSubmit={handleBooking}>
        <div>
          <label>Seat Number:</label>
          <input
            type="number"
            value={seatNumber}
            onChange={(e) => setSeatNumber(Math.max(1, Number(e.target.value)))}
            placeholder="Enter Seat Number"
            min="1"
            required
          />
        </div>

        <div>
          <p>Price per seat: ${seatPrice}</p>
          <p>Total price: ${totalPrice}</p>
        </div>

        <button type="submit" disabled={loading}>
          {loading ? 'Booking...' : 'Book Ticket'}
        </button>
      </form>

      {message && <p>{message}</p>}
    </div>
  );
};

export default Bookticket;
