import React, { useState, useEffect } from "react";
import axios from "axios";

const App = () => {
  const [busId, setBusId] = useState("67890"); // Predefined Bus ID
  const [userId, setUserId] = useState("12345"); // Predefined User ID
  const [seatNumber, setSeatNumber] = useState(""); // Seat number input
  const [price, setPrice] = useState(0); // Price per seat
  const [tickets, setTickets] = useState([]); // Booked tickets
  const [message, setMessage] = useState(""); // Status message

  // Function to fetch bus details
  const handleFetchBusDetails = async () => {
    try {
      const response = await axios.get(`/api/bus/${busId}`); // Replace with your backend route
      setPrice(response.data.pricePerSeat);
      setMessage("Bus details fetched successfully.");
    } catch (error) {
      console.error(error);
      setMessage("Failed to fetch bus details.");
    }
  };

  // Function to book a ticket
  const handleBookTicket = async () => {
    try {
      const response = await axios.post(`/api/tickets/book/${busId}`, {
        userId,
        seatNumber,
      });
      setMessage("Ticket booked successfully!");

      // Fetch updated tickets
      await handleViewTickets();
    } catch (error) {
      console.error(error);
      setMessage("Failed to book ticket.");
    }
  };

  // Function to view tickets
  const handleViewTickets = async () => {
    try {
      const response = await axios.get(`/api/tickets?userId=${userId}`);
      setTickets(response.data);
      setMessage("Tickets retrieved successfully.");
    } catch (error) {
      console.error(error);
      setMessage("Failed to retrieve tickets.");
    }
  };

  useEffect(() => {
    handleFetchBusDetails(); // Fetch bus details on component mount
  }, []);

  return (
    <div>
      <h1>Bus Ticket Booking</h1>

      <div>
        <h2>Book Ticket</h2>
        <input
          type="text"
          placeholder="Seat Number"
          value={seatNumber}
          onChange={(e) => setSeatNumber(e.target.value)}
        />
        <button onClick={handleBookTicket}>Book</button>
      </div>

      <div>
        <h2>Your Tickets</h2>
        <ul>
          {tickets.map((ticket) => (
            <li key={ticket.ticketId}>
              Ticket ID: {ticket.ticketId}, Seat: {ticket.seatNumber}, Bus: {ticket.busId}
            </li>
          ))}
        </ul>
      </div>

      <div>
        <h2>Price</h2>
        <p>Price per seat: {price}</p>
      </div>

      <div>
        <h2>Status</h2>
        <p>{message}</p>
      </div>
    </div>
  );
};

export default App;
