  };

  return (
    <div className="kyc-container">
      <h1>Driver KYC Process</h1>
      <form onSubmit={handleSubmit} className="kyc-form">
        {step === 1 && (
          <div className="kyc-step">
            <label>Driver's Name:</label>
            <input type="text" value={driverName} onChange={(e) => setDriverName(e.target.value)} placeholder="Enter Driver's Name" required />
            <br />
            <label>Phone Number:</label>
            <input type="text" value={phoneNo} onChange={(e) => setPhoneNo(e.target.value)} placeholder="+91XXXXXXXXXX" required />
            <br />
            <label>Email:</label>
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Enter Email" required />
            <br />
            <label>Age:</label>
            <input type="number" value={age} onChange={(e) => setAge(e.target.value)} placeholder="Enter Age" required />
            <br />
            <label>Upload Driver's License:</label>
            <input type="file" onChange={(e) => handleFileUpload(e, setDriverLicense)} required />
            <br />
            <button type="submit">Validate Driver's License</button>
          </div>
        )}
      </form>
      <p className="status-message">Status: {status}</p>
    </div>
  );
};

export default DriverKYC;
