import React, { useState } from "react";
import { bookTicket, viewTickets, cancelTicket, fetchBusDetails } from "./ticketdashboard";

const App = () => {
  const [busId, setBusId] = useState(""); // Bus ID input
  const [userId, setUserId] = useState(""); // User ID input
  const [seatNumber, setSeatNumber] = useState(""); // Number of seats
  const [price, setPrice] = useState(0); // Price per seat
  const [tickets, setTickets] = useState([]); // List of booked tickets
  const [message, setMessage] = useState(""); // Status message

  const handleFetchBusDetails = async () => {
    try {
      const response = await fetchBusDetails(busId);
      setPrice(response.data.pricePerSeat); // Set price per seat from backend
      setMessage("Bus details fetched successfully.");
    } catch (error) {
      console.error(error);
      setMessage("Failed to fetch bus details.");
    }
  };

  const handleBookTicket = async () => {
    try {
      const response = await bookTicket(busId, userId, seatNumber);
      setMessage("Ticket booked successfully!");
      console.log(response.data);
    } catch (error) {
      console.error(error);
      setMessage("Failed to book ticket.");
    }
  };

  const handleViewTickets = async () => {
    try {
      const response = await viewTickets(userId);
      setTickets(response.data);
      setMessage("Tickets retrieved successfully.");
    } catch (error) {
      console.error(error);
      setMessage("Failed to retrieve tickets.");
    }
  };

  const handleCancelTicket = async (ticketId) => {
    try {
      await cancelTicket(ticketId);
      setMessage("Ticket canceled successfully.");
      handleViewTickets(); // Refresh ticket list
    } catch (error) {
      console.error(error);
      setMessage("Failed to cancel ticket.");
    }
  };

  return (
    <div>
      <h1>Bus Ticket Booking</h1>

      <div>
        <h2>Fetch Bus Details</h2>
        <input
          type="text"
          placeholder="Bus ID"
          value={busId}
          onChange={(e) => setBusId(e.target.value)}
        />
        <button onClick={handleFetchBusDetails}>Fetch Details</button>
      </div>

      <div>
        <h2>Book Ticket</h2>
        <input
          type="text"
          placeholder="User ID"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
        />
        <input
          type="text"
          placeholder="Seat Number"
          value={seatNumber}
          onChange={(e) => setSeatNumber(e.target.value)}
        />
        <button onClick={handleBookTicket}>Book</button>
      </div>

      <div>
        <h2>View Tickets</h2>
        <button onClick={handleViewTickets}>View My Tickets</button>
        <ul>
          {tickets.map((ticket) => (
            <li key={ticket.ticketId}>
              Ticket ID: {ticket.ticketId}, Seat: {ticket.seatNumber}, Bus: {ticket.busId}
              <button onClick={() => handleCancelTicket(ticket.ticketId)}>Cancel</button>
            </li>
          ))}
        </ul>
      </div>

      <div>
        <h2>Price</h2>
        <p>Price per seat: {price}</p>
      </div>

      <div>
        <h2>Status</h2>
        <p>{message}</p>
      </div>
    </div>
  );
};

export default App;
