import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const BookTicket = () => {
  const { busId } = useParams();  // Get busId from URL
  const [busDetails, setBusDetails] = useState({});
  const [seatNumber, setSeatNumber] = useState(1);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [availableSeats, setAvailableSeats] = useState(0); // Track available seats
  const seatPrice = 10; // Price per seat (example value)
  const navigate = useNavigate();

  // Fetch bus details
  useEffect(() => {
    const fetchBusDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/buses/${busId}`);
        setBusDetails(response.data);
        setAvailableSeats(response.data.availableSeats);  // Set available seats
      } catch (error) {
        setMessage('Failed to fetch bus details.');
      }
    };
    fetchBusDetails();
  }, [busId]);

  // Handle seat number change
  const handleSeatNumberChange = (e) => {
    const numSeats = Math.max(1, Number(e.target.value));
    setSeatNumber(numSeats);
  };

  // Handle booking
  const handleBooking = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('Booking ticket...');

    if (seatNumber > availableSeats) {
      setMessage('Not enough seats available!');
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post(`http://localhost:5000/api/tickets/book/${busId}`, {
        userId: '12345', // Replace with actual userId from your state/context
        seatNumber,
      });

      if (response.status === 201) {
        setMessage('Ticket booked successfully!');
        setAvailableSeats(availableSeats - seatNumber);  // Decrease available seats
        navigate('/ticketlist');
      }
    } catch (error) {
      setMessage(error.response ? error.response.data.message : 'An error occurred');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Book Ticket for {busDetails.name}</h2>
      <p>From: {busDetails.from}</p>
      <p>To: {busDetails.to}</p>
      <p>Available Seats: {availableSeats}</p>

      <form onSubmit={handleBooking}>
        <div>
          <label>Number of Seats:</label>
          <input
            type="number"
            value={seatNumber}
            onChange={handleSeatNumberChange}
            min="1"
            max={availableSeats}
            required
          />
        </div>

        <div>
          <p>Price per seat: ${seatPrice}</p>
          <p>Total Price: ${seatNumber * seatPrice}</p>
        </div>

        <button type="submit" disabled={loading || seatNumber > availableSeats}>
          {loading ? 'Booking...' : 'Book Ticket'}
        </button>
      </form>

      {message && <p>{message}</p>}
    </div>
  );
};

export default BookTicket;
