import React, { useState } from 'react';
import axios from 'axios'; // Import axios

function KYCProcess() {
    const [step, setStep] = useState(1);
    const [driverInfo, setDriverInfo] = useState({ name: '', phone: '+91', email: 'hello@mail.com' });
    const [driverLicense, setDriverLicense] = useState(null);
    const [idCard, setIdCard] = useState(null);
    const [selfie, setSelfie] = useState(null);
    const [statusMessage, setStatusMessage] = useState('');
    //const [dateOfExpiry, setDateOfExpiry] = useState('');
    const acceptedIdTypes = ['aadhaar', 'passport', 'pan'];
    const [status,setStatus]= useState('');

    const headers = {
        appId: 'yvdxyy',
        appKey: '9epukpvlpl1asl3bym4j',
        transactionId: 'driverkyc'
    };

    const handleFileUpload = (e, setFile) => {
        const file = e.target.files[0];
        if (file && ['image/jpeg', 'image/png', 'application/pdf'].includes(file.type)) {
            setFile(file);
        } else {
            alert('Invalid file type. Please upload JPG, PNG, or PDF.');
        }
    };

    const validateDL = async () => {
        setStatus('Validating Driver License...');
        const formData = new FormData();
        formData.append('image', driverLicense);
        formData.append('countryId', 'ind'); // Replace with the driver's country ID
        formData.append('documentId', 'dl'); // 'DL' for Driver's License
        formData.append('expectedDocumentSide', 'front');

        try {
            const response = await axios.post('https://ind.idv.hyperverge.co/v1/readId', formData, { headers });
            console.log('API Response:', response.data);

            const details = response.data?.result?.details?.[0]?.fieldsExtracted;

            if (details && details.dateOfExpiry?.value) {
                const expiryDate = details.dateOfExpiry.value;
                setDateOfExpiry(expiryDate);

                const expiryDateObj = new Date(expiryDate.split('-').reverse().join('-'));
                if (expiryDateObj > new Date()) {
                    setStatus('Driver License validated successfully!');
                    return true;
                } else {
                    setStatus('Driver License is expired.');
                    return false;
                }
            } else {
                setStatus('Unable to fetch Driver License details.');
                return false;
            }
        } catch (error) {
            console.error('Error validating Driver License:', error);
            setStatus('Validation failed.');
            return false;
        }
    };


    const validateIDCard = async () => {
        if (!idCard) return false;
        const formData = new FormData();
        formData.append('file', idCard);
        const response = await fetch('https://ind.idv.hyperverge.co/v1/readId', {
            method: 'POST',
            body: formData,
            headers
        });
        const result = await response.json();
        return result.isValid && acceptedIdTypes.includes(result.idType.toLowerCase());
    };

    const validateSelfie = async () => {
        if (!selfie) return false;
        const formData = new FormData();
        formData.append('file', selfie);
        const response = await fetch('https://ind.idv.hyperverge.co/v1/checkLiveness', {
            method: 'POST',
            body: formData,
            headers
        });
        const result = await response.json();
        return result.isLive;
    };

    const matchFace = async () => {
        const formData = new FormData();
        formData.append('selfie', selfie);
        formData.append('idCard', idCard);
        const response = await fetch('https://ind.idv.hyperverge.co/v1/matchFace', {
            method: 'POST',
            body: formData,
            headers
        });
        const result = await response.json();
        return result.isMatch;
    };

    const handleSubmitDL = async () => {
        setStatusMessage('Validating Driver License...');

        await validateDL(); // Ensure state updates occur before checking

        if (dateOfExpiry) {
            const expiryDateObj = new Date(dateOfExpiry.split('-').reverse().join('-'));

            if (expiryDateObj > new Date()) {
                setStep(2);
                setStatusMessage(`Driver's License is valid. Expiry Date: ${expiryDate}`);
            } else {
                setStatusMessage(`Driver's License is expired. Expiry Date: ${dateOfExpiry}`);
            }
        } else {
            setStatusMessage('Unable to retrieve expiry date. Validation failed.');
        }
    };


    const handleSubmitID = async () => {
        setStatusMessage('Validating ID Card...');
        const isIDValid = await validateIDCard();
        if (isIDValid) {
            setStep(3);
        } else {
            setStatusMessage('Invalid ID Card.');
        }
    };

    const handleSubmitSelfie = async () => {
        setStatusMessage('Validating selfie...');
        const isSelfieValid = await validateSelfie();
        const isFaceMatch = await matchFace();
        if (isSelfieValid && isFaceMatch) {
            setStatusMessage('KYC process completed successfully.');
            setStep(4);
        } else {
            setStatusMessage('Selfie verification or face match failed.');
        }
    };

    return (
        <div>
            <h2>Driver KYC Process</h2>
            {step === 1 && (
                <div>
                    <h3>Step 1: Basic Information</h3>
                    <input type="text" placeholder="Name" onChange={(e) => setDriverInfo({ ...driverInfo, name: e.target.value })} />
                    <input type="text" placeholder="Phone" value={driverInfo.phone} onChange={(e) => e.target.value.length <= 13 && setDriverInfo({ ...driverInfo, phone: e.target.value })} />
                    <input type="email" placeholder="Email" onChange={(e) => setDriverInfo({ ...driverInfo, email: e.target.value.includes('.') ? e.target.value : e.target.value + '.com' })} />
                    <button onClick={() => setStep(2)}>Next</button>
                </div>
            )}

            {step === 2 && (
                <div>
                    <h3>Step 2: Upload Driver's Licence</h3>
                    <input type="file" accept=".jpg,.png,.pdf" onChange={(e) => handleFileUpload(e, setDriverLicense)} />
                    <button onClick={handleSubmitDL}>Submit</button>
                </div>
            )}

            {step === 3 && (
                <div>
                    <h3>Step 3: Upload ID Card</h3>
                    <input type="file" accept=".jpg,.png,.pdf" onChange={(e) => handleFileUpload(e, setIdCard)} />
                    <button onClick={handleSubmitID}>Submit</button>
                </div>
            )}

            {step === 4 && (
                <div>
                    <h3>Step 4: Upload Selfie</h3>
                    <input type="file" accept=".jpg,.png,.pdf" onChange={(e) => handleFileUpload(e, setSelfie)} />
                    <button onClick={handleSubmitSelfie}>Submit</button>
                </div>
            )}

            <p>{statusMessage}</p>
        </div>
    );
}

export default KYCProcess;
