import React, { useState } from "react";
import { bookTicket, viewTickets, fetchBusDetails } from "./ticketdashboard";

const App = () => {
  const [busId, setBusId] = useState(""); // Bus ID for internal use
  const [userId, setUserId] = useState(""); // User ID for internal use
  const [seatNumber, setSeatNumber] = useState(""); // Number of seats
  const [price, setPrice] = useState(0); // Price per seat
  const [tickets, setTickets] = useState([]); // List of booked tickets
  const [message, setMessage] = useState(""); // Status message

  // Simulated user and bus IDs (for simplicity, hard-coded or fetched from backend in production)
  React.useEffect(() => {
    // Fetch the user ID and bus ID from the backend or session
    setUserId("12345"); // Replace with actual logic to fetch user ID
    setBusId("67890"); // Replace with actual logic to fetch bus ID
  }, []);

  const handleFetchBusDetails = async () => {
    try {
      const response = await fetchBusDetails(busId);
      setPrice(response.data.pricePerSeat); // Set price per seat from backend
      setMessage("Bus details fetched successfully.");
    } catch (error) {
      console.error(error);
      setMessage("Failed to fetch bus details.");
    }
  };

  const handleBookTicket = async () => {
    try {
      const response = await bookTicket(busId, userId, seatNumber);
      setMessage("Ticket booked successfully!");
      console.log(response.data);

      // Automatically fetch tickets after booking
      const ticketResponse = await viewTickets(userId);
      setTickets(ticketResponse.data);
    } catch (error) {
      console.error(error);
      setMessage("Failed to book ticket.");
    }
  };

  return (
    <div>
      <h1>Bus Ticket Booking</h1>

      <div>
        <h2>Fetch Bus Details</h2>
        <button onClick={handleFetchBusDetails}>Fetch Details</button>
      </div>

      <div>
        <h2>Book Ticket</h2>
        <input
          type="text"
          placeholder="Seat Number"
          value={seatNumber}
          onChange={(e) => setSeatNumber(e.target.value)}
        />
        <button onClick={handleBookTicket}>Book</button>
      </div>

      <div>
        <h2>View Tickets</h2>
        <ul>
          {tickets.map((ticket) => (
            <li key={ticket.ticketId}>
              Ticket ID: {ticket.ticketId}, Seat: {ticket.seatNumber}, Bus: {ticket.busId}
            </li>
          ))}
        </ul>
      </div>

      <div>
        <h2>Price</h2>
        <p>Price per seat: {price}</p>
      </div>

      <div>
        <h2>Status</h2>
        <p>{message}</p>
      </div>
    </div>
  );
};

export default App;
